import React from 'react';
import styles from '../styles/Projects.module.css';

const Projects = () => {
    const projects = [
        {
            id: 1,
            title: "Bachelor Thesis: Alzheimer's MRI Image Classification using Deep Learning",
            technologies: ["Python", "PyTorch", "Scikit-learn", "ImageNet models", "NumPy", "Pandas", "CNN"],
            description: "Development of a convolutional neural network (CNN) model to classify brain MRI images and detect patterns associated with Alzheimer's disease.",
            githubUrl: "https://github.com/martacuevasr/Clasification_AlzheimerImages",
            image: "/predicciones.png" // Imagen de la interfaz del clasificador
        },
        {
            id: 2,
            title: "Data Warehouse for Medication Expense Analysis in Hospitals",
            technologies: ["ETL", "Power BI", "OLAP", "MDX"],
            description: "Design of a system for hospital data analysis in the U.S. using ETL pipelines, OLAP cubes, and MDX queries. Development of interactive dashboards in Power BI to extract key insights on pharmaceutical expenses.",
            githubUrl: "https://github.com/AlexSilvaa9/HealthcareDataWarehouse",
            image: "/dashboard.png" // Imagen del dashboard de Power BI
        },
        {
            id: 3,
            title: "Web Application for Skin Disease Detection",
            technologies: ["Python", "CNN", "TensorFlow", "FastAPI"],
            description: "Review of the state of the art in clinical deep learning and development of a web application for detecting skin diseases from images. Implementation with deep vision models and web technologies.",
            githubUrl: "https://github.com/AlexSilvaa9/skin_diseases_detection_web_app",
            image: "/app2.jpeg" // Primera imagen de la app de detección de piel
        }
    ];

    return (
        <div className={styles.projectsPage}>
            <div className={styles.container}>
                <p className={styles.subtitle}>A collection of my work in health engineering and data science</p>

                <div className={styles.projectsGrid}>
                    {projects.map((project) => (
                        <div key={project.id} className={styles.projectCard}>
                            <div className={styles.projectImage}>
                                <img 
                                    src={project.image} 
                                    alt={project.title}
                                    onError={(e) => {
                                        e.target.style.display = 'none';
                                        e.target.nextSibling.style.display = 'flex';
                                    }}
                                />
                                <div className={styles.imagePlaceholder} style={{display: 'none'}}>
                                    <i className="fas fa-code"></i>
                                </div>
                                <div className={styles.projectOverlay}>
                                    <a 
                                        href={project.githubUrl} 
                                        target="_blank" 
                                        rel="noopener noreferrer"
                                        className={styles.githubLink}
                                    >
                                        <i className="fab fa-github"></i>
                                        View Code
                                    </a>
                                </div>
                            </div>

                            <div className={styles.projectContent}>
                                <h3 className={styles.projectTitle}>{project.title}</h3>

                                <div className={styles.technologiesContainer}>
                                    {project.technologies.map((tech, index) => (
                                        <span key={index} className={styles.techTag}>
                                            {tech}
                                        </span>
                                    ))}
                                </div>

                                <p className={styles.projectDescription}>
                                    {project.description}
                                </p>

                                <div className={styles.projectActions}>
                                    <a 
                                        href={project.githubUrl} 
                                        target="_blank" 
                                        rel="noopener noreferrer"
                                        className={styles.actionButton}
                                    >
                                        <i className="fab fa-github"></i>
                                        GitHub
                                    </a>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Projects;
