import React from 'react';
import styles from '../styles/Home.module.css';

const Home = () => {
    const handleSectionChange = (section) => {
        // Buscar el componente App y llamar a su función de cambio de sección
        const event = new CustomEvent('sectionChange', { detail: section });
        window.dispatchEvent(event);
    };

    return (
        <section className={styles.homeBody}>
            <div className={styles.homeContainer}>
                <div className={`${styles.homeImage}`}>
                    <div className={styles.profileImage}>
                        <img 
                            src="/memoji.png" 
                            alt="Marta Cuevas - Memoji" 
                            className={styles.memojiImage}
                        />
                    </div>
                </div>
                <div className={styles.homeContent}>
                    <h1 className={styles.homeTitle}>
                        Hola, soy <span className="highlight">Marta Cuevas</span>
                    </h1>
                    <h2 className={styles.homeSubtitle}>Ingeniera de la Salud</h2>
                    <p className={styles.homeDescription}>
                        Apasionada por la innovación en el sector sanitario y la mejora de la calidad de vida a través de la tecnología.
                    </p>
                    <div className="home-buttons">
                        <button 
                            className="btn btn-primary" 
                            onClick={() => handleSectionChange('contact')}
                        >
                            Contáctame
                        </button>
                        <button 
                            className="btn btn-secondary" 
                            onClick={() => handleSectionChange('about')}
                        >
                            Conoce más
                        </button>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default Home;
