import { useEffect, useRef } from 'react';

const useVanta = () => {
    const vantaRef = useRef(null);

    useEffect(() => {
        if (window.VANTA && !vantaRef.current) {
            vantaRef.current = window.VANTA.NET({
                el: "#vanta-bg",
                mouseControls: true,
                touchControls: true,
                gyroControls: false,
                minHeight: 200.00,
                minWidth: 200.00,
                scale: 1.00,
                scaleMobile: 1.00,
                color: 0x00d4ff,
                backgroundColor: 0x0a0a0a,
                points: 10.00,
                maxDistance: 25.00,
                spacing: 15.00,
                showDots: true
            });
        }

        return () => {
            if (vantaRef.current) {
                vantaRef.current.destroy();
                vantaRef.current = null;
            }
        };
    }, []);

    return vantaRef;
};

export default useVanta;
