import React from 'react';
import LanguageToggle from '../components/LanguageToggle';
import styles from '../styles/Resume.module.css';

const Resume = () => {
    const handleLanguageChange = (lang) => {
        const downloadBtn = document.getElementById('download-cv');
        if (downloadBtn) {
            if (lang === 'es') {
                downloadBtn.href = '/assets/documents/cv-spanish.pdf';
                downloadBtn.innerHTML = '<i class="fas fa-download"></i> Descargar CV';
            } else {
                downloadBtn.href = '/assets/documents/cv-english.pdf';
                downloadBtn.innerHTML = '<i class="fas fa-download"></i> Download CV';
            }
        }
    };

    return (
        <section className="section">
            <div className="container">
                <h2 className="section-title">Currículum</h2>
                <div className={styles.container}>
                    <h2 className={styles.resumeTitle}>Mi Currículum</h2>
                    <div className={styles.resumeLinks}>
                        <LanguageToggle onLanguageChange={handleLanguageChange} />
                        <a 
                            href="/assets/documents/cv-spanish.pdf" 
                            className={`${styles.resumeLink} btn btn-primary`} 
                            id="download-cv" 
                            download
                        >
                            <i className="fas fa-download"></i> Descargar CV
                        </a>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default Resume;
